var tap = require('tap');
var paj_encrypt = require('./../index');

tap.test('MD5 encrypt', function(t){

    var my_password = 'a random Pa$$w0rd';

    var enc_password = paj_encrypt.md5.any(my_password, 'utf8');

    console.log('enc_password = %s', enc_password);

    t.ok(enc_password, 'password not blank');

    var my_other_password = 'another Ent7y a77#mpt'

    var other_enc_password = paj_encrypt.md5.any(my_other_password, 'utf8');

    t.ok(other_enc_password, 'second password not blank');

    t.ok(enc_password != other_enc_password, 'passwords are different');

    t.end();
})

